import React from "react";
import { useState, useEffect, useReducer} from "react";

const initialcount = 0
const initialuser = { name: "", password: "" }
const initialState = {count: 0}
const reducer = (state, action) => {
    switch(action.type){
        case "INCREMENT" :
            return{count: state.count + 1} 
        case "DECREMENT" :
            return{count: state.count - 1}
        default: state
        }
   }
const UseState = () => {
    const [count, setCount] = useState(initialcount)
    const [user, setUser] = useState(initialuser)
    const [state, dispatch] = useReducer(initialState, reducer)

    useEffect(() =>{
        document.title = `count:${count}`
    })
    const Count = () => {
        setCount(count + 1)
    }

    const Submit = () => {
        setUser(user)
        console.log(user)
    }
    return (
        <div>
            <div> count : {count}</div>
            <button type="button" onClick={Count}>Count</button><br></br>
            name:<input type="text" value={user.name} onChange={e => setUser({...user, name: e.target.value})}/><br></br>
            password:<input type="password" value={user.password} onChange={e => setUser({...user,password:e.target.value})}/><br></br>
            <button type="btn" onClick={Submit}>submit</button>
            <div>{state.count}</div>

        </div>
    )
}
export default UseState
