import React, { useState } from "react";

import { useNavigate } from "react-router-dom";

import { useForm } from "react-hook-form";



const Register = ({ name }) => {
    const [userInfo, setUserinfo] = useState()
    const [user, setUser] = useState([])
    const [isError, setIsError] = useState("")



    const navigate = useNavigate()

    if (name !== "user") {
        throw new Error("something went wrong!!")
    }
    // const Register = (e) => {
    //     e.preventDefault()
    //     setUser(user)
    //     navigate('/login')
    //     console.log(user)

    // }


    const { register, handleSubmit, formState: { errors } } = useForm()
    const Submit = (data) => {
        setUserinfo(data)
        console.log(data)
        console.log(errors)
    }

    return (
        <div className="main">
            <h2>Register {name}</h2>
            <form>
                <div className="input">
                    <label>Firstname:</label>
                    <input type="text" {...register("Firstname", { required: "Firstname is required" })} />
                </div>
                <div className="input">
                    <label>Lastname:</label>
                    <input type="text" {...register("Lastname", { required: "Lastname is required" })} />
                </div>
                <div className="input">
                    <label>Username:</label>
                    <input type="text" {...register("username", { required: "username is required" })} />
                </div>
                {errors.username && <div style={{ color: "red" }}>{errors.username.message}</div>}
                <div className="input">
                    <label>Email:</label>
                    <input className="emailInput" type="text" {...register("email", { required: "email is required", pattern: { value: /^[\w-\.]+@([\w-]+\.)+[\w-]{2,4}$/, message: "please enter valid email" } })} />
                </div>
                {errors.email && <div style={{ color: "red" }}>{errors.email.message}</div>}
                <div className="input">
                    <label>phone_No:</label>
                    <input type="number" {...register("Phone_No", { required: "phone no is required" })} />
                </div>
                <div className="input">
                    <label>Password:</label>
                    <input type="password" {...register("password", { required: "password is required", pattern: { value: /^(?=.*[a-z])(?=.*[A-Z])(?=.*\d)[a-zA-Z\d]{8,}$/, message: "please enter valid password" } })} />
                </div>
                {errors.password && <div style={{ color: "red" }}>{errors.password.message}</div>}
                <button onClick={handleSubmit(Submit)}>register</button>
            </form>

        </div>
    )
}

export default Register
