const ProductReducer = (state, action) =>{
    switch(action.type){
        case "SET_LOADING" :
            return{
                ...state,
                isLodding: true,
            }
        case "API_ERROR" :
            return{
                ...state,
                isLodding: false,
                isError: true
            }
        default: 
        return state;
    }

}
export default ProductReducer
