import React, {useEffect} from "react";
import { useParams } from "react-router-dom";
import axios from "axios";
import { useDispatch, useSelector } from "react-redux";
import { seletedProducts } from "../../redux/actions/ProductAction";
import { StepTitle } from "semantic-ui-react";

const ProductView = () =>{
    const product = useSelector(state => state.product)
    const {productid} = useParams()
    const dispatch =  useDispatch()
    console.log(product)

    const getProductDetail = async() =>{
        const res = await axios.get(`https://fakestoreapi.com/products/${productid}`)
        .catch(error => {
            console.log(error)
        })
        dispatch(seletedProducts(res.data))
    }

    useEffect(()=>{
        if(productid && productid !== "") getProductDetail();
    }, [])
    const { image, price, title, category, description } = product
    return(
        <div className="ui grid container">
            {Object.keys(product).length === 0 ?(
                <div>...Loading</div>
            ) : (
            <div className="ui placeholder segment">
                <div className="ui two column stackable center aligned grid">
                    <div className="ui vertical divider">AND</div>
                    <div className="middle aligned row">
                        <div className="column 1p">
                            <img className=" ui fluid image" src={image}/>
                        </div>
                        <div className="column rp">
                            <h1>{title}</h1>
                            <h2>
                                <a className="ui teal tag label">${price}</a>
                            </h2>
                            <h3 className="ui brown block header">{category}</h3>
                            <p>{description}</p>
                            <div className="ui vertical animated button" tabIndex="0">
                                <div className="hidden content">
                                    <i className=" shop icon"></i>
                                </div>
                                <div className="visible content"> Add to Cart</div>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
            )}
        </div>
    )
}
export default ProductView
