    isLodding: false,
    isError: false,
    products: [],

} 

const getProducts = async (url) => {
    const res = await axios.get(url);
    const Products = await res.data;   
    console.log(Products)
}
export const AppProvider = ({children}) =>{

    const [state, dispatch] = useReducer(reducer, initialState)


    useEffect(()=> {
        getProducts(API);
    }, []);

