import React from "react";

import { useState, useEffect, useReducer } from "react";

const initialcount = 0

const initialuser = { name: "", password: "" }

const initialState = { Number: 0 }

const reducer = (state, action) => {

    switch (action.type) {
        case "INCREMENT":
            return { Number: state.Number + 1 }
        case "DECREMENT":
            return { Number: state.Number - 1 }
        default: return state
    }
}

const UseState = () => {

    const [count, setCount] = useState(initialcount)

    const [user, setUser] = useState(initialuser)

    const [state, dispatch] = useReducer(reducer, initialState)

    useEffect(() => {
        document.title = `count:${count}`
    })

    const Countplus = () => {
        setCount(count + 1)

    }

    const Countminus = () => {
        setCount(count - 1)

    }

    const Submit = () => {
        setUser(user)
        console.log(user)
    }

    return (

        <div>

            <div> count : {count}</div>
            <button type="button" onClick={Countplus}>Count</button><br></br>
            <button type="button" onClick={Countminus}>Count</button><br></br>
            name:<input type="text" value={user.name} onChange={e => setUser({ ...user, name: e.target.value })} /><br></br>
            password:<input type="password" value={user.password} onChange={e => setUser({ ...user, password: e.target.value })} /><br></br>
            <button type="btn" onClick={Submit}>submit</button>
            <div>number :{state.Number}</div>
            <button type="btn" onClick={() => dispatch({ type: "INCREMENT" })}>increment</button>
            <button type="btn" onClick={() => dispatch({ type: "DECREMENT" })}>decrement</button>

        </div>
    )
}

export default UseState
