import { createContext, useEffect } from "react";

export const AppContext = createContext()

const API = "http://202.131.117.92:7152/api/product?page=10&limit=20&search=true"

const getProducts = async (url) => {
    const res = await axios.get(url);
        console.log(res)
}
export const AppProvider = ({children}) =>{

    useEffect(()=> {
        getProducts(API);
    }, []);

    return(
        <AppContext.Provider value="bhavik">
            {children}
        </AppContext.Provider>
    )
}
