import React from "react";

import { Form, Button } from 'semantic-ui-react';

//import { useNavigate } from "react-router-dom";

import { useForm } from "react-hook-form";



const Register = ({ name }) => {



    // const navigate = useNavigate()

    if (name !== "user") {
        throw new Error("something went wrong!!")
    }
    const Register = (data) => {
        data.preventDefault()

        console.log(data);


    }


    const { register, handleSubmit, formState: { errors } } = useForm()

    return (
        <div className="main">
            <h2>Register {name}</h2>
            <Form>
                <Form.Field>
                    <label>Firstname:</label>
                    <input type="text" {...register("Firstname", { required: "Firstname is required" })} />
                </Form.Field>
                {errors.firstname && <p>Please check the First Name</p>}
                <Form.Field>
                    <label>Lastname:</label>
                    <input type="text" {...register("Lastname", { required: "Lastname is required" })} />
                </Form.Field>
                {errors.lastname && <p>Please check the First Name</p>}
                <Form.Field>
                    <label>Username:</label>
                    <input type="text" {...register("username", { required: "username is required" })} />
                </Form.Field>
                {errors.username && <p>Please check the First Name</p>}
                <Form.Field>
                    <label>Email:</label>
                    <input className="emailInput" type="text" {...register("email", { required: "email is required", pattern: { value: /^[\w-\.]+@([\w-]+\.)+[\w-]{2,4}$/, message: "please enter valid email" } })} />
                </Form.Field>
                {errors.email && <p>Please check the First Name</p>}
                <Form.Field>
                    <label>Password:</label>
                    <input type="password" {...register("password", { required: "password is required", pattern: { value: /^(?=.*[a-z])(?=.*[A-Z])(?=.*\d)[a-zA-Z\d]{8,}$/, message: "please enter valid password" } })} />
                </Form.Field>
                {errors.password && <div style={{ color: "red" }}>{errors.password.message}</div>}
                <Button onClick={handleSubmit(Register)}>register</Button>
            </Form>

        </div>
    )
}

export default Register
