const ProductReducer = (state, action) => {
    switch (action.type) {
        case "SET_LOADING":
            return {
                ...state,
                isLoading: true,
            }

        case "SET_API_PRODUCTS":

            const products = action.payload.filter((currentProduct) => {
                return currentProduct === true;
            })
            return {
                ...state,
                isLoading: false,
                Products: action.payload,
            }
        case "API_ERROR":
            return {
                ...state,
                isLoading: false,
                isError: true
            }
        default: {
            return state;
        }
    }

}
export default ProductReducer
